import { Component, OnInit } from "@angular/core";
import { ROUTES } from "../sidebar/sidebar.component";
import { Location } from "@angular/common";
import { ToastrService } from "ngx-toastr";

var misc: any = {
  sidebar_mini_active: true
};
@Component({
  selector: "app-auth-navbar",
  templateUrl: "./auth-navbar.component.html",
  styleUrls: ["./auth-navbar.component.scss"]
})
export class AuthNavbarComponent implements OnInit {
  isCollapsed = true;
  private listTitles: any[];
  location: Location;

  constructor(location: Location, public toastr: ToastrService) {
    this.location = location;
  }
  minimizeSidebar() {
    const body = document.getElementsByTagName("body")[0];
    if (body.classList.contains("sidebar-mini")) {
      misc.sidebar_mini_active = true;
    } else {
      misc.sidebar_mini_active = false;
    }
    if (misc.sidebar_mini_active === true) {
      body.classList.remove("sidebar-mini");
      misc.sidebar_mini_active = false;
      this.showSidebarMessage("Sidebar mini deactivated...");
    } else {
      body.classList.add("sidebar-mini");
      this.showSidebarMessage("Sidebar mini activated...");
      misc.sidebar_mini_active = true;
    }

    // we simulate the window Resize so the charts will get updated in realtime.
    const simulateWindowResize = setInterval(function() {
      window.dispatchEvent(new Event("resize"));
    }, 180);

    // we stop the simulation of Window Resize after the animations are completed
    setTimeout(function() {
      clearInterval(simulateWindowResize);
    }, 1000);
  }
  showSidebarMessage(message) {
    this.toastr.show(
      '<span data-notify="icon" class="tim-icons icon-bell-55"></span>',
      message,
      {
        timeOut: 4000,
        closeButton: true,
        enableHtml: true,
        toastClass: "alert alert-danger alert-with-icon",
        positionClass: "toast-top-right"
      }
    );
  }
  ngOnInit() {
    this.listTitles = ROUTES.filter(listTitle => listTitle);
  }
  getTitle() {
    var titlee = this.location.prepareExternalUrl(this.location.path());
    titlee = titlee.split("/")[2];

    for (let i = 0; i < this.listTitles.length; i++) {
      if (this.listTitles[i].type === "sub") {
        for (let j = 0; j < this.listTitles[i].children.length; j++) {
          if (this.listTitles[i].children[j].path === titlee) {
            return this.listTitles[i].children[j].title;
          }
        }
      }
    }

    return "Black Dashboard PRO Angular";
  }
}

<nav class=" navbar navbar-expand-lg navbar-absolute fixed-top" [ngClass]="{'bg-white': isCollapsed === false, 'navbar-transparent': isCollapsed === true}">
  <div class=" container-fluid">
    <div class=" navbar-wrapper">
      <div class=" navbar-toggle d-inline">
        <button class=" navbar-toggler" type="button">
          <span class=" navbar-toggler-bar bar1">
          </span>
          <span class=" navbar-toggler-bar bar2">
          </span>
          <span class=" navbar-toggler-bar bar3">
          </span>
        </button>
      </div>
      <a class=" navbar-brand" href="javascript:void(0)">
        {{ getTitle() }}
      </a>
    </div>
    <button aria-label="Toggle navigation" class=" navbar-toggler" [attr.aria-expanded]="!isCollapsed" (click)="isCollapsed = !isCollapsed" id="navigation" type="button">
      <span class=" navbar-toggler-bar navbar-kebab">
      </span>
      <span class=" navbar-toggler-bar navbar-kebab">
      </span>
      <span class=" navbar-toggler-bar navbar-kebab">
      </span>
    </button>
    <div class=" navbar-collapse" [collapse]="isCollapsed" id="navigation">
      <ul class=" navbar-nav ml-auto">
        <li class=" nav-item">
          <a class=" nav-link text-primary" [routerLink]="['/dashboard']">
            <i class=" tim-icons icon-minimal-left">
            </i>
            Back to Dashboard
          </a>
        </li>
        <li class=" nav-item">
          <a class=" nav-link" [routerLink]="['/pages/register']">
            <i class=" tim-icons icon-laptop">
            </i>
            Register
          </a>
        </li>
        <li class=" nav-item">
          <a class=" nav-link" [routerLink]="['/pages/login']">
            <i class=" tim-icons icon-single-02">
            </i>
            Login
          </a>
        </li>
        <li class=" nav-item active">
          <a class=" nav-link" [routerLink]="['/pages/pricing']">
            <i class=" tim-icons icon-coins">
            </i>
            Pricing
          </a>
        </li>
        <li class=" nav-item">
          <a class=" nav-link" [routerLink]="['/pages/lock']">
            <i class=" tim-icons icon-lock-circle">
            </i>
            Lock
          </a>
        </li>
      </ul>
    </div>
  </div>
</nav>
