import { Component, OnInit } from "@angular/core";
import { ToastrService } from "ngx-toastr";

@Component({
  selector: "app-fixed-plugin",
  templateUrl: "./fixed-plugin.component.html",
  styleUrls: ["./fixed-plugin.component.scss"]
})
export class FixedPluginComponent implements OnInit {
  public sidebarColor: string = "blue";
  public state: boolean = true;
  public dashboardColor: boolean = true;
  constructor(public toastr: ToastrService) {}
  changeSidebarColor(color) {
    var sidebar = document.getElementsByClassName("sidebar")[0];
    var mainPanel = document.getElementsByClassName("main-panel")[0];

    this.sidebarColor = color;

    if (sidebar != undefined) {
      sidebar.setAttribute("data", color);
    }
    if (mainPanel != undefined) {
      mainPanel.setAttribute("data", color);
    }
  }
  changeDashboardColor(color) {
    var body = document.getElementsByTagName("body")[0];
    if (body && color === "white-content") {
      body.classList.add(color);
    } else if (body.classList.contains("white-content")) {
      body.classList.remove("white-content");
    }
  }
  ngOnInit() {}
  onChangeDashboardColor(event) {
    const body = document.getElementsByTagName("body")[0];
    if (this.dashboardColor === true) {
      this.changeDashboardColor("");
    } else {
      this.changeDashboardColor("white-content");
    }
    // we simulate the window Resize so the charts will get updated in realtime.
    var simulateWindowResize = setInterval(function() {
      window.dispatchEvent(new Event("resize"));
    }, 180);

    // we stop the simulation of Window Resize after the animations are completed
    setTimeout(function() {
      clearInterval(simulateWindowResize);
    }, 1000);
  }
  onChange(event) {
    const body = document.getElementsByTagName("body")[0];
    if (this.state === true) {
      body.classList.remove("sidebar-mini");
      this.showSidebarMessage("Sidebar mini deactivated...");
    } else {
      body.classList.add("sidebar-mini");
      this.showSidebarMessage("Sidebar mini activated...");
    }
    // we simulate the window Resize so the charts will get updated in realtime.
    var simulateWindowResize = setInterval(function() {
      window.dispatchEvent(new Event("resize"));
    }, 180);

    // we stop the simulation of Window Resize after the animations are completed
    setTimeout(function() {
      clearInterval(simulateWindowResize);
    }, 1000);
  }
  showSidebarMessage(message) {
    this.toastr.show(
      '<span class="now-ui-icons ui-1_bell-53"></span>',
      message,
      {
        timeOut: 4000,
        closeButton: true,
        enableHtml: true,
        toastClass: "alert alert-danger alert-with-icon",
        positionClass: "toast-top-right"
      }
    );
  }
}
<div class=" fixed-plugin">
  <div class="show-dropdown dropdown" dropdown [insideClick]="true">
    <a
      id="button-basic"
      data-toggle="dropdown"
      dropdownToggle
      class="dropdown-toggle"
      aria-controls="dropdown-basic"
    >
      <i class=" fa fa-cog fa-2x"> </i>
    </a>

    <ul
      id="dropdown-basic"
      *dropdownMenu
      class="dropdown-menu"
      role="menu"
      aria-labelledby="button-basic"
    >
      <li class=" header-title">Sidebar Background</li>
      <li class=" adjustments-line">
        <a class=" switch-trigger background-color" href="javascript:void(0)">
          <div class=" badge-colors text-center">
            <span
              class=" badge filter badge-primary"
              [ngClass]="{ active: sidebarColor === 'primary' }"
              (click)="changeSidebarColor('primary')"
            >
            </span>
            <span
              class=" badge filter badge-info"
              [ngClass]="{ active: sidebarColor === 'blue' }"
              (click)="changeSidebarColor('blue')"
            >
            </span>
            <span
              class=" badge filter badge-success"
              [ngClass]="{ active: sidebarColor === 'green' }"
              (click)="changeSidebarColor('green')"
            >
            </span>
            <span
              class="badge filter badge-warning"
              [ngClass]="{ active: sidebarColor === 'orange' }"
              (click)="changeSidebarColor('orange')"
            ></span>
            <span
              class=" badge filter badge-danger"
              [ngClass]="{ active: sidebarColor === 'red' }"
              (click)="changeSidebarColor('red')"
            >
            </span>
          </div>
          <div class=" clearfix"></div>
        </a>
      </li>
      <li class="header-title">Sidebar Mini</li>
      <li class="adjustments-line">
        <div class="togglebutton switch-sidebar-mini">
          <span class="label-switch">OFF</span>
          <bSwitch
            [switch-on-color]="'primary'"
            [switch-off-text]="' '"
            [switch-on-text]="' '"
            [(ngModel)]="state"
            (onChangeState)="onChange($event)"
            (click)="onChange($event)"
          ></bSwitch>
          <span class="label-switch label-right">ON</span>
        </div>
      </li>
      <li class=" adjustments-line text-center color-change">
        <div class="togglebutton switch-sidebar-mini">
          <span class="label-switch"> LIGHT MODE </span>
          <bSwitch
            [switch-on-color]="'primary'"
            [switch-off-text]="' '"
            [switch-on-text]="' '"
            [(ngModel)]="dashboardColor"
            (onChangeState)="onChangeDashboardColor($event)"
            (click)="onChangeDashboardColor($event)"
          ></bSwitch>
          <span class="label-switch label-right"> DARK MODE </span>
        </div>
      </li>
      <li class="button-container mt-4">
        <a
          href="http://www.themclabs.com"
          class="btn btn-default btn-block btn-round"
        >
          Documentation
        </a>
      </li>
    </ul>
  </div>
</div>
