import {
    Component,
    Output,
    Input,
    EventEmitter,
    ElementRef
} from '@angular/core';

@Component({
    selector: 'ng2-dropdown-button',
    styleUrls: [ './style.scss' ],
    templateUrl: './template.html'
})
export class Ng2DropdownButton {
    @Output() public onMenuToggled: EventEmitter<boolean> = new EventEmitter<boolean>();
    @Input() public showCaret: boolean = true;

    constructor(private element: ElementRef) {}

    /**
     * @name toggleMenu
     * @desc emits event to toggle menu
     */
    public toggleMenu(): void {
        this.onMenuToggled.emit(true);
    }

    /**
     * @name getPosition
     * @desc returns position of the button
     */
    public getPosition(): ClientRect {
      return this.element.nativeElement.getBoundingClientRect();
    }
}
import { Ng2MenuItem } from '../menu-item/ng2-menu-item';
import { Ng2DropdownMenu } from './ng2-dropdown-menu';
import { Ng2DropdownState } from '../../services/ng2-dropdown-state';

const KEYS = {
    BACKSPACE: 9,
    PREV: 38,
    NEXT: 40,
    ENTER: 13,
    ESCAPE: 27
};

/**
 * @name onSwitchNext
 * @param index
 * @param items
 * @param state
 */
const onSwitchNext = (index: number, items: Ng2MenuItem[], state: Ng2DropdownState) => {
    if (index < items.length - 1) {
        state.select(items[index + 1], true);
    }
};

/**
 * @name onSwitchPrev
 * @param index
 * @param items
 * @param state
 */
const onSwitchPrev = (index: number, items: Ng2MenuItem[], state: Ng2DropdownState) => {
    if (index > 0) {
        state.select(items[index - 1], true);
    }
};

/**
 * @name onBackspace
 * @param index
 * @param items
 * @param state
 */
const onBackspace = (index: number, items: Ng2MenuItem[], state: Ng2DropdownState) => {
    if (index < items.length - 1) {
        state.select(items[index + 1], true);
    } else {
        state.select(items[0], true);
    }
};

function onEscape(this: Ng2DropdownMenu) {
    this.hide();
};

/**
 * @name onItemClicked
 * @param index
 * @param items
 * @param state
 */
const onItemClicked = (index: number, items: Ng2MenuItem[], state: Ng2DropdownState) => {
    return state.selectedItem ? state.selectedItem.click() : undefined;
};

export const ACTIONS = {
    [KEYS.BACKSPACE]: onBackspace,
    [KEYS.PREV]: onSwitchPrev,
    [KEYS.NEXT]: onSwitchNext,
    [KEYS.ENTER]: onItemClicked,
    [KEYS.ESCAPE]: onEscape
};

export function arrowKeysHandler(event): void {
    if ([38, 40].indexOf(event.keyCode) > -1) {
        event.preventDefault();
    }
}
import { EventEmitter } from '@angular/core';
import { Ng2MenuItem } from '../components/menu-item/ng2-menu-item';
export class Ng2DropdownState {
    public onItemSelected: EventEmitter<Ng2MenuItem> = new EventEmitter<
        Ng2MenuItem
    >();
    public onItemClicked: EventEmitter<Ng2MenuItem> = new EventEmitter<
        Ng2MenuItem
    >();
    public onItemDestroyed: EventEmitter<Ng2MenuItem> = new EventEmitter<
        Ng2MenuItem
    >();

    private _selectedItem: Ng2MenuItem;

    /**
     * @name selectedItem
     * @desc getter for _selectedItem
     */
    public get selectedItem(): Ng2MenuItem {
        return this._selectedItem;
    }

    /**
     * @name selects a menu item and emits event
     * @param item
     */
    public select(item: Ng2MenuItem | undefined, dispatchEvent = true): void {
        this._selectedItem = item;

        if (!dispatchEvent || !item) {
            return;
        }

        item.focus();

        this.onItemSelected.emit(item);
    }

    /**
     * @name unselect
     * @desc sets _selectedItem as undefined
     */
    public unselect(): void {
        this._selectedItem = undefined;
    }
}
import { Injectable } from '@angular/core';
import { Ng2DropdownState } from './ng2-dropdown-state';

@Injectable()
export class DropdownStateService {
    public menuState = {
        isVisible: false,
        toString(): string {
            return this.isVisible === true ? 'visible' : 'hidden';
        }
    };

    public dropdownState = new Ng2DropdownState();
}
import { Component, Input, ElementRef, OnDestroy } from '@angular/core';
import { DropdownStateService } from '../../services/dropdown-state.service';

@Component({
    selector: 'ng2-menu-item',
    styleUrls: ['./style.scss'],
    template: `
        <div
            class="ng2-menu-item"
            role="button"
            tabindex="0"
            [class.ng2-menu-item--selected]="isSelected"
            (keydown.enter)="click()"
            (click)="click()"
            (mouseover)="select()"
        >
            <ng-content></ng-content>
        </div>
    `
})
export class Ng2MenuItem implements OnDestroy {
    /**
     * @preventClose
     * @desc if true, clicking on the item won't close the dropdown
     */
    @Input() public preventClose = false;

    /**
     * @name value
     * @desc any value associated to the item
     */
    @Input() public value: any;

    constructor(
        private state: DropdownStateService,
        private element: ElementRef
    ) {}

    public ngOnDestroy(): void {
        this.state.dropdownState.onItemDestroyed.emit(this);
    }

    /**
     * @name isSelected
     * @desc returns current selected item
     */
    public get isSelected(): boolean {
        return this === this.state.dropdownState.selectedItem;
    }

    /**
     * @name click
     * @desc emits select event
     */
    public select($event?): void {
        this.state.dropdownState.select(this, true);

        if ($event) {
            $event.stopPropagation();
            $event.preventDefault();
        }
    }

    /**
     * @name click
     * @desc emits click event
     */
    public click(): void {
