            ? dc.documentElement.scrollLeft
            : dc.body.scrollLeft;

        const y = supportPageOffset
            ? wd.pageYOffset
            : isCSS1Compat
            ? dc.documentElement.scrollTop
            : dc.body.scrollTop;

